import React, { useEffect, useState } from 'react';
import jwt_decode from 'jwt-decode';
import LogoInicio from '../Images/LogoInicio.jpg';
import UserImg from '../Images/iconoPerfil.png';


function Login() {
  //A FUTURO GUARDAR ESTO EN ALGO MAS GLOBAL COMO EL REDUCER NO EN EL STATE
  const [user, setUser] = useState({});

  function handlerCallBackResponse(response) {
    var userObject = jwt_decode(response.credential);
    setUser(userObject);
    document.getElementById('signInDiv').hidden = true;
  }

  function handleSignOut(event) {
    setUser({});
    document.getElementById('signInDiv').hidden = false;
  }

  useEffect(() => {
    /*global google*/
    google.accounts.id.initialize({
      client_id: "292987655880-i0jgdsstnjkhir74hdif5cbnndjlak60.apps.googleusercontent.com",
      callback: handlerCallBackResponse
    });

    google.accounts.id.renderButton(
      document.getElementById('signInDiv'),
      { theme: 'outline', size: 'large' }
    );
  }, []);

  return (
    <div className="container">

      <div class="row " >
        <div class="col align-self-start">
          <img src={LogoInicio} className="imgLogo" alt="LogoDL" />
        </div>
        <div class="col d-flex align-self-center justify-content-end">
          <img src={UserImg} className="UserImg" alt="User" />
        </div>
        <div class="">
          <h1 class="display-1">Display 1</h1>
        </div>

        <div class=" card col d-flex justify-content-center">
          <div class="row">
            <div class="col-md-12">
              <h3 class="tituloInforme" >Login</h3>
            </div>
          </div>
          <div class="row">
            <div class="col-md-12">
              <a class="btn btn-lg btn-google btn-block text-uppercase btn-outline" id="signInDiv" href="#"><img src="https://img.icons8.com/color/16/000000/google-logo.png" /> Signup Using Google</a>

            </div>
          </div>
          <br />

          <p class="text-inverse text-center">Already have an account? <a href="<?= base_url() ?>auth/login" data-abc="true">Login</a></p>
        </div>
      </div>

      <div id="signInDiv"></div>
      {
        Object.keys(user).length !== 0 &&
        <button onClick={(e) => handleSignOut(e)}>SignOut</button>
      }
      {user &&
        <div >

          <img src={user.picture} alt="googleUserPic"></img>
          <h2 >{user.name}</h2>
        </div>
      }
    </div>
  );
}

export default Login
